mapper.select:
SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_TITLE, BOARD_REGISTER_DATE
FROM
(
    SELECT ROWNUM R, BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_REGISTER_DATE
    FROM
    (
        SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_REGISTER_DATE
        FROM TBL_BOARD B
        WHERE USER_NUMBER = #{userNumber} AND BOARD_CATEGORY = #{boardCategory} ORDER BY BOARD_NUMBER DESC
    ) D2

WHERE ROWNUM <= #{page} * #{amount}
) WHERE R > (#{page} - 1) * #{amount}

SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT
FROM TBL_BOARD
WHERE BOARD_NUMBER = #{boardNumber}

mapper.update:
UPDATE TBL_BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, BOARD_UPDATE_DATE = SYSDATE
WHERE BOARD_NUMBER = #{boardNumber}

mapper.select:
SELECT BOARD_NUMBER, USER_NUMBER, BOARD_TITLE, BOARD_REGISTER_DATE, BOARD_VIEW
FROM
(
    SELECT ROWNUM R, BOARD_NUMBER, USER_NUMBER,  BOARD_TITLE, BOARD_REGISTER_DATE, BOARD_VIEW
    FROM
    (
        SELECT BOARD_NUMBER, USER_NUMBER, BOARD_TITLE, BOARD_REGISTER_DATE, BOARD_VIEW
        FROM TBL_BOARD B
        ORDER BY BOARD_NUMBER
    ) D2

WHERE ROWNUM <= #{page} * #{amount}
) WHERE R > (#{page} - 1) * #{amount}

mapper.update:
UPDATE TBL_BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUMBER = #{boardNumber}
