mapper.update:
UPDATE TBL_USER
SET USER_PASSWORD = #{userPassword}, USER_NICKNAME = #{userNickname}
WHERE USER_NUMBER = #{userNumber}

mapper.select:
SELECT USER_PASSWORD
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail}

SELECT COUNT(USER_EMAIL)
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail}

SELECT COUNT(USER_NICKNAME)
FROM TBL_USER
WHERE USER_NICKNAME = #{userNickname}

SELECT USER_NUMBER
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail} AND USER_PASSWORD = ${userPassword}

SELECT USER_PASSWORD, USER_NAME, USER_NICKNAME
FROM TBL_USER
WHERE USER_NUMBER = #{userNumber}

mapper.update:
UPDATE TBL_USER
SET USER_PASSWORD = #{userPassword}, USER_NAME = #{userName}, USER_NICKNAME = #{userNickname}
WHERE USER_NUMBER = #{userNumber}
