mapper.select:
SELECT USER_PASSWORD
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail}

SELECT COUNT(USER_EMAIL)
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail}

SELECT COUNT(USER_NICKNAME)
FROM TBL_USER
WHERE USER_NICKNAME = #{userNickname}

SELECT USER_NUMBER
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail} AND USER_PASSWORD = ${userPassword}
